import React from "react";
import axios from 'axios';
import app from '../app.json'

const {APIHOST} = app;

export default class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
    };
  }

  login() {

  axios.post(`${APIHOST}/auth/login`, {
    //localhost:3001/usuarios/login la ruta para obtener el token
    email: this.state.email,
    password: this.state.password,
  })
  .then((response) => {
    if (isNull()) {
      //Si genera alerta - es porque es nulo el token
      alert('usuario y/o contraseña invalidas');
    }
    else {//almacena el token en la variable  _s
        
      this.props.history.push(window.open('/index')) //login ok ? redirije a empleados
    }
  })       

  .catch((err) => {
    console.log(err);
  });
// alert(`Usuario: ${this.state.usuario} -password: ${this.state.pass}`); //traer la variable de usuario para mostrar en el alert
}


render() {
  return (
    <div
      className="container text-center"
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "80vh",
      }}
    >
      <div
        className="card text-center"
        style={{
          width: "18rem",
          backgroundColor: "#b9fac4",
        }}
      >
        <div className="card-header">
          <h2 className="text-success">Inicia sesion</h2>
        </div>
        <div className="m-3">
          <input
            type="text"
            className="form-control"
            id="email"
            placeholder="Correo electronico"
          />
        </div>
        <div className="mx-3">
          <input
            type="password"
            className="form-control"
            id="password"
            placeholder="Contraseña"
          />
        </div>
        <div className="my-3">
          <button type="submit" className="btn btn-success">
            Iniciar sesion
          </button>
        </div>
      </div>
    </div>
 );
}
}
